import Foundation
import UIKit

class Pogoji {
    
    private init() {}
    
    static func getPogoji() -> NSAttributedString {
        
        let titleAttributes = [NSAttributedStringKey.font : UIFont.boldSystemFont(ofSize: 18), NSAttributedStringKey.foregroundColor : UIColor.airabelaBlue]
        
        let paragraphAttributes = [NSAttributedStringKey.font : UIFont.systemFont(ofSize: 16)]
        
        let pogojiText = NSMutableAttributedString(string: "SPLOŠNI POGOJI UPORABE MOBILNE APLIKACIJE\n\n", attributes: titleAttributes)
        pogojiText.append(NSAttributedString(string: "1. UVODNE DOLOČBE\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "1.1. S Splošnimi pogoji uporabe mobilne aplikacije Reklamacije (v nadaljevanju: splošni pogoji uporabe), Airabela d.o.o., Šmartinska cesta 58A, 1000 Ljubljana (v nadaljevanju: Airabela), ki je vpisana v sodnem registru Okrožnega sodišča v Novi Gorici, št. reg. vložka 2012/23929, matična številka: 6176470000, ID št. za DDV: SI79240518; osnovni kapital 30.000,00 EUR določa obveznosti, pravice in pogoje za uporabo in poslovanje preko mobilne aplikacije Reklamacije.\n\n1.2. Mobilna aplikacija Reklamacije (v nadaljevanju: mobilna aplikacija) je last podjetja Airabela.\n\n1.3. Uporabnik mobilne aplikacije (v nadaljevanju: uporabnik) je lahko katerakoli oseba, ki si je mobilno aplikacijo namestila na mobilno napravo in je obenem zaposlena v podjetju, ki je poslovni partner podjetja Airabela. Oseba, ki ne izpolnjuje pogoja iz prejšnje povedi, mobilne aplikacije ne more uporabljati.\n\n1.4. Mobilna aplikacija omogoča uporabniku enostavnejše in hitrejše pošiljanje reklamacijskih zapisnikov za enote proizvajalca Daikin v garancijskem roku.\n\n1.5. Storitev, ki se izvaja preko mobilne aplikacije, se izvaja na podlagi teh splošnih pogojev uporabe. Splošni pogoji uporabe so objavljeni na sedežu podjetja Airabela. Uporabnik pa lahko do njih dostopa tudi znotraj mobilne aplikacije.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "2. NAMESTITEV MOBILNE APLIKACIJE\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "2.1. Mobilna aplikacija ni namenjena širši javnosti, zato tudi ni javno dostopna. Uporabnik mora za pridobitev mobilne aplikacije poslati prošnjo podjetju Airabela na elektronski naslov administracija@airabela.si. Airabela pošlje uporabniku na njegov elektronski naslov namestitveno datoteko s kratkimi in enostavnimi navodili za namestitev.\n2.2. Airabela ne odgovarja za nastanek kakršnekoli škode ali izgube dohodka iz naslova uporabe mobilne aplikacije pridobljene na spletu.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "3. TEHNIČNE ZAHTEVE MOBILNE APLIKACIJE\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "3.1. Mobilna aplikacija deluje na operacijskih sistemih Android (Google) z minimalno verzijo Android 4.0.3 (Ice Cream Sandwich).\n3.2. Za dostop do funkcionalnosti mobilne aplikacije mora uporabnik pred prvo uporabo v mobilno aplikacijo vnesti podatke o podjetju in kontaktni osebi.\n3.3. Uporabnik mora imeti nameščeno aplikacijo za upravljanje z elektronsko pošto v naprotnem primeru preko mobilne aplikacije ni mogoče poslati reklamacijskega zapisnika.\n3.4. Ostale storitve ali aplikacije, ki se uporabljajo znotraj mobilne aplikacije so Kamera in Galerija oziroma druga aplikacija, preko katere uporabnik upravlja in hrani fotografije.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "4. UPORABA MOBILNE APLIKACIJE\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "4.1. Mobilna aplikacija je uporabniku na voljo brezplačno.\n\n4.2. Uporabnik za uporabo mobilne aplikacije potrebuje omogočeno Wi-Fi ali podatkovno povezavo na mobilni napravi. Če pri uporabi mobilne aplikacije pride do prenosa mobilnih podatkov je uporabnik dolžan plačati prenos mobilnih podatkov, ki mu ga izbrani ponudnik elektronskih komunikacijskih storitev zaračuna v skladu z veljavnim cenikom.\n\n4.3. Določene funkcionalnosti so v mobilni aplikaciji omogočene preko neodvisne zunanje storitve ali aplikacije. Za uporabo teh storitev in aplikacij veljajo pogoji uporabe zunanje storitve, ki jih določa lastnik storitve ali aplikacije, Airabela pa v zvezi s tem ne prevzema nobene odgovornosti in ne daje nobenih jamstev.\n\n4.4. Za dostop do funkcionalnosti mobilne aplikacije mora uporabnik pred prvo uporabo v mobilno aplikacijo vnesti podatke o podjetju in kontaktni osebi. Ti podatki se shranijo v mobilno napravo uporabnika kar omogoča hitro in enostavno pošiljanje reklamacijskih zapisnikov brez vsakokratnega vpisovanja omenjenih podatkov. Mobilna aplikacija oziroma Airabela podatkov uporabnika ne hrani oziroma ne obdeluje in niti nima dostopa do le-teh.\n\n4.5. Za varnost in zaupnost vseh podatkov, shranjenih na mobilni napravi, odgovarja izključno uporabnik. Airabela ne prevzema nobene odgovornosti za kakršnokoli zlorabo podatkov, ki so shranjeni na mobilni napravi. Airabela ne prevzema nobene odgovornosti za škodo, ki bi nastala zaradi nepravilnega in neprevidnega ravnanja uporabnika z mobilno aplikacijo ali mobilno napravo.\n\n4.6. Uporabnik se zavezuje, da bo zavaroval dostop do svoje mobilne naprave in da ne bo nikoli puščal mobilne naprave nenadzorovane z dostopno mobilno aplikacijo, da ne pride do zlorab. Če uporabnik ve, da je prišlo do zlorabe oziroma nepooblaščene uporabe mobilne aplikacije je o tem dolžan nemudoma obvestiti Airabelo.\n\n4.7. Postopek oddaje reklamacijskega zapisnika preko mobilne aplikacije:\n\n- Uporabnik najprej ustvari nov reklamacijski zapisnik s pritiskom na gumb »REKLAMACIJSKI ZAPISNIK«.\n\n- Nato preko nekaj spustnih seznamov izbere podatke, ki ustrezajo reklamirani enoti in nadaljuje s pritiskom na gumb »NADALJUJ«.\n\n- V naslednjem koraku uporabnik vpiše serijske številke (če so potrebne) ter kodo in opis napake na reklamirani enoti. Nadaljuje s pritiskom na gumb »NADALJUJ«.\n\n- V naslednjem koraku lahko uporabnik reklamacijskemu zapisniku doda fotografije (doda lahko že obstoječe fotografije ali pa ustvari nove) oziroma nadaljuje brez fotografij. V naslednji korak nadaljuje s pritiskom na ustrezen gumb.\n\n- V tem koraku je uporabnik preusmerjen na zunanjo storitev oziroma aplikacijo za pošiljanje elektronske pošte. Odvisno od nastavitev na mobilni napravi se mu bo le-ta avtomatično zagnala ali pa se bo prikazal seznam aplikacij iz katerega bo uporabnik izbral pravo. Vsebina elektronskega sporočila bo že izpolnjena zato uporabnik v tem koraku samo pritisne gumb, s katerim pošlje reklamacijski zapisnik podjetju Airabela. V kolikor je bil reklamacijski zapisnik uspešno dostavljen na elektronski naslov podjetja Airabela bo uporabnik o tem obveščen z avtomatiziranim povratnim sporočilom, ki ga bo prejel na elektronski naslov, s katerega je poslal reklamacijski zapisnik.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "5. REŠEVANJE REKLAMACIJSKIH ZAPISNIKOV\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "5.1. Uporabnik se zavezuje, da bo mobilno aplikacijo uporabljal zgolj za posredovanje reklamacijskih zapisnikov za enote v garancijskem roku in da bo reklamacijskemu zapisniku vedno dodal fotografije razen kadar je dodajanje le-teh zaradi narave napake nesmiselno oziroma iz njih napaka ne bi bila razvidna.\n\n5.2. Enote imajo garancijo, če je tako navedeno na garancijskem listu ali na računu. Poleg navedbe garancije so navedeni tudi garancijski roki, ki veljajo za posamezne enote.\n\n5.3. Garancija je veljavna, če je enoto montirala za to pooblaščena oseba, če so upoštevana navodila za montažo in uporabo s strani proizvajalca ter ob predložitvi s strani Airabele podpisanega garancijskega lista, ki se nahaja na zadnji strani navodil za uporabo ter ob predložitvi računa.\n\n5.4. Rok za rešitev reklamacije narekuje veljavna zakonodaja o varstvu potrošnikov. Z namenom rešitve reklamacije v roku je uporabnik dolžan zahtevo potrošnika za odpravo napak na enoti, preko reklamacijskega zapisnika podjetju Airabela sporočiti takoj oziroma čim prej, ko ugotovi izvor napake.\n\n5.5. Če uporabnik po oddaji reklamacijskega zapisnika preko mobilne aplikacije ne prejme avtomatiziranega povratnega elektronskega sporočila o uspešni dostavi se smatra, da reklamacijski zapisnik ni bil poslan. V tem primeru je dolžnost uporabnika, da na oddelku za reševanje reklamacij preko e-poštnega naslova podpora@airabela.si preveri ali je podjetje Airabela prejelo njegov reklamacijski zapisnik. V kolikor ugotovi, da podjetje Airabela reklamacijskega zapisnika ni prejelo je uporabnik le-tega dolžan poslati ponovno. Airabela ne odgovarja za nastanek kakršnekoli škode ali izgube dohodka iz naslova nerešene reklamacije za katero ni prejela reklamacijskega zapisnika.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "6. DELOVANJE IN DOSTOPNOST MOBILNE APLIKACIJE\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "6.1. Airabela si bo prizadevala vzdrževati mobilno aplikacijo tako, da bo delovala nemoteno in brezhibno. Airabela ne odgovarja za morebitno škodo ali neprijetnosti, s katerimi bi se uporabnik soočil zaradi prekinitve delovanja ali motenega delovanja mobilne aplikacije.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "7. SPREMEMBA MOBILNE APLIKACIJE\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "7.1. Zaradi tehničnih ali funkcionalnih nadgradenj mobilne aplikacije ima Airabela pravico do izdaje nove verzije mobilne aplikacije. Uporabnik bo o njej obveščen preko elektronskega naslova s katerim se je prijavil za uporabo mobilne aplikacije. Uporabnik bo na ta elektronski naslov prejel novo namestitveno datoteko, s katero bo na svoj mobilni telefon namestil novo verzijo mobilne aplikacije.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "8. OMEJITEV IN IZKLJUČITEV ODGOVORNOSTI\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "8.1. Airabela ne jamči za pravilnost in popolnost vsebine na mobilni aplikaciji.\n\n8.2. Airabela ne prevzema nobene odgovornosti glede funkcionalnosti in kakovosti mobilne aplikacije. Airabela prav tako ne prevzema nobene obveznosti in odgovornosti za stvarne ali pravne napake ter kakršnekoli druge morebitne pomanjkljivosti mobilne aplikacije.\n\n8.3. Airabela ne zagotavlja, da mobilna aplikacija ustreza vsem uporabnikovim zahtevam in potrebam. Prav tako Airabela ne zagotavlja nemotenega delovanja mobilne aplikacije in ne prevzema nobene obveznosti in odgovornosti v primeru izpada omrežij, izpada električne energije ali drugih tehničnih motenj, ki bi lahko začasno motile delovanje mobilne aplikacije, ter ne odgovarja za primere višje sile.\n\n8.4. Uporabnik v celoti odgovarja za ustrezno zaščito vseh podatkov na mobilni napravi. Airabela ne odgovarja za škodo v primeru izgube teh podatkov na mobilni napravi in za nedelovanje mobilne aplikacije, če uporabnik ne izpolnjuje tehničnih in drugih pogojev za njeno uporabo.\n\n8.5. Airabela ne jamči, da mobilna aplikacija deluje brezhibno in brez prekinitev. Airabela prav tako ne jamči, da mobilna aplikacije deluje na vseh mobilnih napravah in v kombinaciji z vsako strojno in programsko opremo.\n\n8.6. Airabela nadalje tudi ne prevzema nobene odgovornosti glede zanesljivosti in kakovosti povezave mobilne aplikacije z internetom oziroma zanesljivosti in kakovosti prenosa na internetu ali drugem komunikacijskem kanalu.\n\n8.7. Airabela ne odgovarja za škodo, ki nastane kot posledica napačnega ravnanja z mobilno aplikacijo (kar vključuje tudi omogočen dostop nepooblaščenim osebam do uporabe mobilne aplikacije in zlorabo mobilne aplikacije) ali napačnega vnosa podatkov s strani uporabnika mobilne aplikacije.\n\n8.8. Airabela ne odgovarja za nastanek kakršnekoli škode ali izgube dohodka iz naslova uporabe mobilne aplikacije.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "9. PODPORA UPORABNIKOM MOBILNE APLIKACIJE\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "9.1. Airabela v svojem delovnem času uporabnikom zagotavlja podporo v zvezi z uporabo mobilne aplikacije, in sicer na telefonski številki: 08 205 30 25 in elektronskem naslovu: administracija@airabela.si.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "10. PRENEHANJE DELOVANJA MOBILNE APLIKACIJE\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "10.1. Airabela lahko kadarkoli preneha zagotavljati dostop do mobilne aplikacije, njeno delovanje in podporo, vendar mora uporabnike o tem obvestiti na primeren način (npr. z obvestilom preko elektronske pošte).\n\n10.2. Uporabnik ima pravico kadarkoli prenehati uporabljati mobilno aplikacijo.\n\n10.3. Airabela ima pravico brez predhodnega opozorila onemogočiti dostop do mobilne aplikacije oziroma njeno uporabo vsakemu uporabniku, ki krši te splošne pogoje uporabe.\n\n10.4. V primeru prenehanja zagotavljanja dostopa do mobilne aplikacije oziroma delovanja uporabniku avtomatično preneha pravica do uporabe mobilne aplikacije. Uporabnik je dolžan nemudoma prenehati uporabljati mobilno aplikacijo, prav tako pa je dolžan uničiti oziroma izbrisati mobilno aplikacijo na svoji mobilni napravi in vse njene morebitne kopije.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "11. PRAVICE INTELEKTUALNE LASTNINE\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "11.1. S posredovanjem namestitvene datoteke Airabela na uporabnika ne prenaša nobene avtorske pravice ali druge pravice intelektualne lastnine v zvezi z mobilno aplikacijo (med drugim tudi ne pravice do uporabe slogana, logotipa, imena), temveč mu podeljuje zgolj neprenosljivo in neizključno pravico do uporabe mobilne aplikacije izključno na svoji mobilni napravi, vse na način, v obsegu in pod pogoji, kot so določeni v teh splošnih pogojih uporabe. Uporabnik zlasti ne sme izdelovati kopij mobilne aplikacije, namestiti mobilne aplikacije na strežnik ali drugo platformo, kakorkoli distribuirati mobilne aplikacije ali omogočiti dostopa do mobilne aplikacije prek omrežja, dajati mobilne aplikacije v najem ali podeljevati podlicenc za njeno uporabo ter prevesti, prilagoditi, prirediti ali kako drugače predelati mobilne aplikacije.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "12. VAROVANJE OSEBNIH PODATKOV\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "12.1. Airabela se v skladu z zakonom o varovanju osebnih podatkov (ZVOP-1) zavezuje, da v kolikor bo preko uporabe mobilne aplikacije prišla do osebnih podatkov uporabnikov, jih bo skrbno in trajno varovala.\n\n12.2. Airabela bo osebne podatke pridobljene na ta način uporabila izključno za potrebe obveščanja uporabnikov v povezavi z mobilno aplikacijo ter za potrebe reševanja reklamacije in ostalo potrebno komunikacijo razen v primerih, ko je zakonsko določena obveznost posredovanja teh podatkov. Podatki kupca so lahko z namenom rešitve reklamacije posredovani tudi našim pogodbenim partnerjem, npr. dostavni službi, vendar jih ti ne shranjujejo v svojih zbirkah.\n\n12.3. Osebni podatki poslovnih partnerjev v nobenem primeru ne bodo predani nepooblaščenim osebam.\n\n12.4. Za varovanje osebnih podatkov je odgovoren tudi poslovni partner sam in sicer tako, da poskrbi za varnost vnosa svojih podatkov v mobilno aplikacijo, tako da ti podatki niso vidni drugim nepooblaščenim osebam.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "13. VELJAVNOST SPLOŠNIH POGOJEV UPORABE\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "13.1. Ti splošni pogoji uporabe stopijo v veljavo z dnem 01.01.2018.\n\n13.2. Pravice in obveznosti uporabnika po teh splošnih pogojih uporabe začnejo veljati od dneva, ko si je uporabnik namestil mobilno aplikacijo na mobilno napravo.\n\n13.3. Splošni pogoji uporabe veljajo za vse uporabnike mobilne aplikacije in jih v celoti zavezujejo.\n\n13.4. Vsak uporabnik mobilne aplikacije soglaša z vsebino in uporabo splošnih pogojev uporabe.\n\n13.5. V primeru nadgradnje, izdaje nove verzije ali kakršnekoli druge spremembe mobilne aplikacije veljajo splošni pogoji uporabe tudi za te nadgradnje, nove verzije ali druge spremembe mobilne aplikacije, v kolikor Airabela izrecno ne določi drugače.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "14. SPREMEMBE SPLOŠNIH POGOJEV UPORABE\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "14.1. Airabela si pridržuje pravico, da kadarkoli spremeni ali dopolni splošne pogoje uporabe.\n\n14.2. Uporabnik bo o spremembah splošnih pogojev uporabe obveščen preko elektronske pošte, in sicer na elektronski naslov iz katerega je poslal prošnjo podjetju Airabela  za pridobitev mobilne aplikacije. Hkrati bo prejel tudi novo namestitveno datotekonadgrajene mobilne aplikacije, ki bo vključevala nove splošne pogoje uporabe. V primeru, da uporabnik nagrajene mobilne aplikacije ne namesti na svojo mobilno napravo, to ne pomeni, da zanj novi splošni pogoji uporabe ne veljajo.\n\n14.3. V kolikor se uporabnik s spremenjenimi ali dopolnjenimi splošnimi pogoji uporabe ne bi strinjal, je dolžan mobilno aplikacijo nemudoma prenehati uporabljati in jo izbrisati iz svoje mobilne naprave. V kolikor uporabnik ne preneha uporabljati mobilne aplikacije in je ne izbriše iz svoje mobilne naprave se šteje, da se s spremembo ali dopolnitvijo splošnih pogojev uporabe strinja.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "15. SALVATORNA KLAVZULA\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "15.1. Morebitna neveljavna določba ali nezmožnost njene izvršitve ne vpliva na veljavnost drugih določb in veljavnost splošnih pogojih uporabe kot celote. V primeru neveljavnosti ali nezmožnosti izvrševanja določbe splošnih pogojih uporabe, bo takšno določbo nadomestila razlaga, ki je najbližja namenu takšne neveljavne oziroma neizvršljive določbe.\n\n", attributes: paragraphAttributes))
        pogojiText.append(NSAttributedString(string: "16. KONČNA DOLOČILA\n\n", attributes: titleAttributes))
        pogojiText.append(NSAttributedString(string: "16.1. V primeru nedovoljene uporabe mobilne aplikacije oziroma kakršnihkoli drugih zlorab pri dostopu do nje oziroma njeni uporabi ima Airabela pravico izvesti vse ukrepe in druga dejanja, ki jih v skladu s svojo poslovno presojo šteje za potrebne zaradi varstva svojih pravic in interesov in/ali zaradi varstva pravic in interesov katerekoli druge osebe (npr. preprečitev dostopa do mobilne aplikacije).\n\n16.2. Morebitne spore med Airabelo in uporabnikom v zvezi z uporabo mobilne aplikacije bosta Airabela in uporabnik reševala sporazumno. V kolikor to ne bi bilo mogoče, je za reševanje sporov pristojno stvarno pristojno sodišče v Ljubljani. V primeru spora se uporablja pravo Republike Slovenije.\n\n", attributes: paragraphAttributes))
        
        return pogojiText
        
    }
    
}

